import React, { Suspense, useRef, useState, useEffect } from "react"
import { Canvas, useFrame } from "react-three-fiber"
import { ContactShadows, Environment, useGLTF, OrbitControls } from "drei"
import { HexColorPicker } from "react-colorful"
import { proxy, useProxy } from "valtio"


const state = proxy({
  current: null,
  items: {
    hip: '#ffffff',
    shoulder: '#ffffff',
    calf_and_low: '#ffffff',
    back_thigh: '#ffffff',
    nose: '#ffffff',
    back: '#ffffff',
    head: '#ffffff',
    front_thigh: '#ffffff',
    neck: '#ffffff',
    hand_tissue: '#ffffff',
    chest_and_abs: '#ffffff',
    front_lower_leg: '#ffffff',
    inner_upper_thigh: '#ffffff',
    feet_finger: '#ffffff',
    lowerForearm: '#ffffff',
    biceps: '#ffffff',
  }
})


function Model(props) {
  const group = useRef();
  const snap = useProxy(state);
  const { nodes, materials } = useGLTF('scene.gltf')
  return (
    <group ref={group} {...props} dispose={null}>
      <group rotation={[-Math.PI / 2, 0, 0]}>
        <mesh material-color={snap.items.hip} material={materials.hip} geometry={nodes.mesh_0.geometry} />
        <mesh material-color={snap.items.shoulder} material={materials.shoulder} geometry={nodes.mesh_1.geometry} />
        <mesh material-color={snap.items.calf_and_low} material={materials.calf_and_low} geometry={nodes.mesh_2.geometry} />
        <mesh material-color={snap.items.back_thigh} material={materials.back_thigh} geometry={nodes.mesh_3.geometry} />
        <mesh material-color={snap.items.nose} material={materials.nose} geometry={nodes.mesh_4.geometry} />
        <mesh material-color={snap.items.back} material={materials.back} geometry={nodes.mesh_5.geometry} />
        <mesh material-color={snap.items.hip} material={materials.head} geometry={nodes.mesh_6.geometry} />
        <mesh material={materials.head} geometry={nodes.mesh_7.geometry} />
        <mesh material-color={snap.items.neck}material={materials.neck} geometry={nodes.mesh_8.geometry} />
        <mesh material-color={snap.items.hand_tissue} material={materials.hand_tissue} geometry={nodes.mesh_9.geometry} />
        <mesh material-color={snap.items.chest_and_abs} material={materials.chest_and_abs} geometry={nodes.mesh_10.geometry} />
        <mesh material-color={snap.items.front_lower_leg} material={materials.front_lower_leg} geometry={nodes.mesh_11.geometry} />
        <mesh material-color={snap.items.inner_upper_thigh} material={materials.inner_upper_thigh} geometry={nodes.mesh_12.geometry} />
        <mesh material-color={snap.items.feet} material={materials.feet_finger} geometry={nodes.mesh_13.geometry} />
        <mesh material-color={snap.items.lowerForearm} material={materials.lowerForearm} geometry={nodes.mesh_14.geometry} />
        <mesh material-color={snap.items.biceps} material={materials.biceps} geometry={nodes.mesh_15.geometry} />
      </group>
    </group>
  )
}

const Light = () => {
  return(
    <>
    {/* Ambient Light illuminates lights for all objects */}
    <ambientLight intensity={0.2} />
    {/* Diretion light */}
    <directionalLight position={[17, 10, 5]} intensity={0.5} />
    <directionalLight
      castShadow
      position={[0, 10, 0]}
      intensity={1.5}
      shadow-mapSize-width={1024}
      shadow-mapSize-height={1024}
      shadow-camera-far={50}
      shadow-camera-left={-10}
      shadow-camera-right={10}
      shadow-camera-top={10}
      shadow-camera-bottom={-10}
    />
    {/* Spotlight Large overhead light */}
    <pointLight position={[-10, 0, -20]} intensity={0.7} />
    <pointLight position={[0, -10, 0]} intensity={1.5} />
    <spotLight intensity={1} position={[1000, 0, 0]} castShadow />
  </>

  )
}
export default function App() {
  return (
    <>
      <Canvas  camera={{ position:[0,0,120], fov:65}}>
       <Light/>
        <Suspense fallback={null}>
          <Model/>
        </Suspense>
      </Canvas>
    </>
  )
}
